import moment, { duration } from "moment";
import React, { useState, useEffect } from "react";
import useInterval from "react-useinterval";
import { Icon } from "semantic-ui-react";
import styled from "styled-components";
import { BlogLinks } from "../contents/links";
import { customMedia } from "../styles/globalStyle";
import { theme } from "../styles/theme";
import { ITime } from "../type/types";
interface Props {
  countdownDate: string;
}

const LockdropInfo: React.FC<Props> = (props: Props) => {
  const [countdown, setCountdown] = useState<ITime>({
    Days: 0,
    Hours: 0,
    Mins: 0,
    Secs: 0
  });

  // Memo: Optional
  // call {addZeros(mins)}
  const addZeros = (value: number): string => {
    let time = String(value);
    while (time.length < 2) {
      time = `0${time}`;
    }
    return time;
  };

  const runCountdown = (): void => {
    // Memo: FutureDate: UTC
    const futureDate = moment.utc(props.countdownDate);
    const today = moment();

    const clockDuration = duration(futureDate.diff(today));

    const days = Math.floor(clockDuration.asDays());
    const hours = clockDuration.hours();
    const mins = clockDuration.minutes();
    const secs = clockDuration.seconds();

    setCountdown({
      Days: days,
      Hours: hours,
      Mins: mins,
      Secs: secs
    });
  };


  useEffect(() => {
    runCountdown();
    // eslint-disable-next-line
  }, []);

  useInterval(() => {
    runCountdown();
  }, 1000);

  return (
    <LockdropInfoContainer>
      <TitleH1>Lockdrop Information</TitleH1>
      <div className="expired">
        <ExpiredH2>Expired:</ExpiredH2>
        <div className="time">
          {Object.keys(countdown).map(
            (key: string, i: number): JSX.Element => (
              <div key={i} className="time-column">
                {/*
              // @ts-ignore */}
                <HeaderTimeSpan>{countdown[key]}</HeaderTimeSpan>
                <p>{key}</p>
              </div>
            )
          )}
        </div>
      </div>
      <div className="total">
        <AmountH2>Total Amount Lock:</AmountH2>
        <TotalAmountH2>888</TotalAmountH2>
        <span className="currency">ETH</span>
      </div>
      <div className="reference">
        <Icon name="attention" className="icon" />
        <a
          href={BlogLinks.lockdropIntroduction}
          rel="noopener noreferrer"
          target="_blank"
        >
          What is the Lockdrop?
        </a>
      </div>
    </LockdropInfoContainer>
  );
};

export default LockdropInfo;

const LockdropInfoContainer = styled.div`
  width: 630px;
  height: 300px;
  display: grid;
  align-items: center;
  background-color: silver;
  border: 4px solid ${theme.colors.black};
  border-radius: 10px;
  padding: 20px 10px;

  ${customMedia.lessThan("tabletSmall")`
      width: 558px;
  `}
  ${customMedia.lessThan("mobile")`
      min-width: 330px;
      width: 90%;
      height: 250px;
      border: 3px solid ${theme.colors.black};
      margin 0;
      padding: 10px 4px;
  `}

  .expired, .total {
    display: grid;
    align-items: center;
    grid-template-columns: 60% 30% 30%;
    padding: 8px 2%;
    height: 42px;
    ${customMedia.lessThan("tabletSmall")`
      grid-template-columns: 70% 15% 15%;
      padding: 0 4px;
    `}
  }
  .expired {
    grid-template-columns: 45% 55%;
    ${customMedia.lessThan("tabletSmall")`
      grid-template-columns: 35% 65%;
    `};
    .time-column {
      width: 56px;
      text-align: center;
    }
  }

  .currency {
    font-size: 18px;
  }
  .warning {
    height: 8px;
    padding-bottom: 6px;
    color: ${theme.colors.red};
    font-size: 14px;
    display: grid;
    align-items: center;
    grid-template-columns: 50% 50%;
    ${customMedia.lessThan("tabletSmall")`
      font-size: 8px;
    `}
  }
  .time {
    display: flex;
    justify-content: space-around;
    text-align: center;
    p {
      ${customMedia.lessThan("mobile")`
      font-size: 12px;
    `}
    }
  }
  .reference {
    margin-top: 30px;
    .icon {
      margin-left: 34px;
      font-size: 16px;
      ${customMedia.lessThan("tabletSmall")`
      margin-left: 4px;
      font-size: 14px;
    `}
    }
    a {
      margin-left: 10px;
      font-size: 16px;
      color: ${theme.colors.black};
      cursor: pointer;
      ${customMedia.lessThan("tabletSmall")`
        margin-left: 4px;
        font-size: 14px;
      `}
      &:hover {
        border-bottom: solid 2px ${theme.colors.blue};
      }
    }
  }
`;

const TitleH1 = styled.h1`
  text-align: center;
  margin-bottom: 30px;
  ${customMedia.lessThan("mobile")`
    font-size: 24px;
  `}
`;

const ExpiredH2 = styled.h2`
  margin-bottom: 0px;
  ${customMedia.lessThan("mobile")`
    font-size: 18px;
  `}
`;
const AmountH2 = styled(ExpiredH2)``;

const TotalAmountH2 = styled.h2`
  margin: 0px;
  padding: 0px;
  ${customMedia.lessThan("mobile")`
    font-size: 18px;
  `}
`;

const HeaderTimeSpan = styled.span`
  font-weight: bold;
  font-size: 22px;
  opacity: 0.9;
  ${customMedia.lessThan("mobile")`
    font-size: 18px;
  `}
`;
